mapper.select:
SELECT -- 입시 요강 정보 A.ADMISSION_ID, A.ADMISSION_YEAR, A.ADMISSION_TYPE, A.EARLY_ADMISSION, A.REGULAR_ADMISSION, A.SCHOOL_ID, A.DEPARTMENT_ID, A.ADMISSION_MEMO, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 학교 정보 B.SCHOOL_NAME, -- 학과 정보 C.DEPARTMENT_NAME, -- 등록한 사람, 수정한 사람 D.USER_NAME AS CREATED_USER_NAME, D.USER_PHONE AS CREATED_USER_PHONE, E.USER_NAME AS UPDATED_USER_NAME, E.USER_PHONE AS UPDATED_USER_PHONE FROM ADMISSIONS A LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = A.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = A.DEPARTMENT_ID LEFT JOIN USERS D ON D.USER_ID = A.CREATED_BY LEFT JOIN USERS E ON E.USER_ID = A.UPDATED_BY WHERE 1=1
AND ( A.ADMISSION_MEMO LIKE CONCAT('%', #{keyword}, '%') )
AND A.ADMISSION_YEAR = #{admissionYear}
AND A.ADMISSION_TYPE = #{admissionType}
AND A.SCHOOL_ID = #{schoolId}
AND A.DEPARTMENT_ID = #{departmentId}
ORDER BY A.ADMISSION_YEAR ASC, A.ADMISSION_TYPE ASC, B.SCHOOL_NAME ASC, C.DEPARTMENT_NAME ASC LIMIT #{offset}, #{listCountPerPage}

mapper.update:

UPDATE ADMISSIONS SET ADMISSION_ID = #{admissionId}



    , ADMISSION_YEAR = #{admissionYear}




    , ADMISSION_TYPE = #{admissionType}




    , EARLY_ADMISSION = #{earlyAdmission}




    , REGULAR_ADMISSION = #{regularAdmission}




    , SCHOOL_ID = #{schoolId}




    , DEPARTMENT_ID = #{departmentId}




    , ADMISSION_MEMO = #{admissionMemo}




    , UPDATED_BY = #{updatedBy}



WHERE ADMISSION_ID = #{admissionId} LIMIT 1

mapper.select:

SELECT COUNT(*) 
FROM ADMISSIONS A
WHERE 1=1



    AND A.ADMISSION_MEMO LIKE CONCAT('%', #{keyword}, '%')




    AND A.ADMISSION_YEAR = #{admissionYear}




    AND A.ADMISSION_TYPE = #{admissionType}




    AND A.SCHOOL_ID = #{schoolId}




    AND A.DEPARTMENT_ID = #{departmentId}
