mapper.select:
SELECT -- 지점 정보 A.BRANCH_ID, A.BRANCH_NAME, A.BRANCH_ADDRESS, A.BRANCH_PHONE, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 등록한 사람, 수정한 사람 B.USER_NAME AS CREATED_USER_NAME, B.USER_PHONE AS CREATED_USER_PHONE, C.USER_NAME AS UPDATED_USER_NAME, C.USER_PHONE AS UPDATED_USER_PHONE FROM BRANCHES A LEFT JOIN USERS B ON B.USER_ID = A.CREATED_BY LEFT JOIN USERS C ON C.USER_ID = A.UPDATED_BY WHERE 1=1
AND ( A.BRANCH_NAME LIKE CONCAT('%', #{keyword}, '%') OR A.BRANCH_ADDRESS LIKE CONCAT('%', #{keyword}, '%') OR A.BRANCH_PHONE LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY BRANCH_NAME ASC LIMIT #{offset}, #{listCountPerPage}

mapper.update:

UPDATE BRANCHES SET BRANCH_ID = #{branchId}



    , BRANCH_NAME = #{branchName}




    , BRANCH_ADDRESS = #{branchAddress}




    , BRANCH_PHONE = #{branchPhone}



WHERE BRANCH_ID = #{branchId} LIMIT 1
