mapper.select:
SELECT -- 구/군 정보 A.DISTRICT_ID, A.PROVINCE_ID, A.DISTRICT_NAME, A.DISTRICT_MEMO, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 등록한 사람, 수정한 사람 B.USER_NAME AS CREATED_USER_NAME, B.USER_PHONE AS CREATED_USER_PHONE, C.USER_NAME AS UPDATED_USER_NAME, C.USER_PHONE AS UPDATED_USER_PHONE, -- 시/도 정보 D.PROVINCE_NAME FROM DISTRICTS A LEFT JOIN USERS B ON B.USER_ID = A.CREATED_BY LEFT JOIN USERS C ON C.USER_ID = A.UPDATED_BY LEFT JOIN PROVINCES D ON D.PROVINCE_ID = A.PROVINCE_ID WHERE 1=1
AND ( A.DISTRICT_NAME LIKE CONCAT('%', #{keyword}, '%') OR A.DISTRICT_MEMO LIKE CONCAT('%', #{keyword}, '%') OR D.PROVINCE_NAME LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY D.PROVINCE_NAME ASC, A.DISTRICT_NAME ASC LIMIT #{offset}, #{listCountPerPage}

mapper.update:

UPDATE DISTRICTS SET DISTRICT_ID = #{districtId}



    , PROVINCE_ID = #{provinceId}




    , DISTRICT_NAME = #{districtName}




    , DISTRICT_MEMO = #{districtMemo}




    , UPDATED_BY = #{updatedBy}



WHERE DISTRICT_ID = #{districtId} LIMIT 1
