mapper.select:
SELECT -- 입시 요강 정보 A.CRITERIA_ID, A.YEAR, A.CRITERIA_GUBUN, A.SCHOOL_ID, A.DEPARTMENT_ID, A.ACCEPTED_COUNT, A.CSAT_REFLECTED_SCORE, A.PHYSICAL_REFLECTED_SCORE, A.INTERNAL_REFLECTED_SCORE, A.INTERVIEW_REFLECTED_SCORE, A.TOTAL_REFLECTED_SCORE, A.CRITERIA_MEMO, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 학교 정보 B.SCHOOL_NAME, -- 학과 정보 C.DEPARTMENT_NAME, -- 등록한 사람, 수정한 사람 D.USER_NAME AS CREATED_USER_NAME, D.USER_PHONE AS CREATED_USER_PHONE, E.USER_NAME AS UPDATED_USER_NAME, E.USER_PHONE AS UPDATED_USER_PHONE FROM CRITERIAS A LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = A.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = A.DEPARTMENT_ID LEFT JOIN USERS D ON D.USER_ID = A.CREATED_BY LEFT JOIN USERS E ON E.USER_ID = A.UPDATED_BY WHERE 1=1
AND ( A.CRITERIA_GUBUN LIKE CONCAT('%', #{keyword}, '%') OR A.CRITERIA_MEMO LIKE CONCAT('%', #{keyword}, '%') OR B.SCHOOL_NAME LIKE CONCAT('%', #{keyword}, '%') OR C.DEPARTMENT_NAME LIKE CONCAT('%', #{keyword}, '%') )
AND A.YEAR = #{year}
ORDER BY A.YEAR DESC, B.SCHOOL_NAME DESC, C.DEPARTMENT_NAME DESC DESC LIMIT #{offset}, #{listCountPerPage}

mapper.update:

UPDATE CRITERIAS SET CRITERIA_ID = #{criteriaId}



    , YEAR = #{year}




    , CRITERIA_GUBUN = #{criteriaGubun}




    , SCHOOL_ID = #{schoolId}




    , DEPARTMENT_ID = #{departmentId}




    , ACCEPTED_COUNT = #{acceptedCount}




    , CSAT_REFLECTED_SCORE = #{csatReflectedScore}




    , PHYSICAL_REFLECTED_SCORE = #{physicalReflectedScore}




    , INTERNAL_REFLECTED_SCORE = #{internalReflectedScore}




    , INTERVIEW_REFLECTED_SCORE = #{interviewReflectedScore}




    , TOTAL_REFLECTED_SCORE = #{totalReflectedScore}




    , CRITERIA_MEMO = #{criteriaMemo}




    , UPDATED_BY = #{updatedBy}



WHERE CRITERIA_ID = #{criteriaId} LIMIT 1

mapper.select:
SELECT COUNT(*) FROM CRITERIAS A LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = A.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = A.DEPARTMENT_ID WHERE 1=1
AND ( A.CRITERIA_GUBUN LIKE CONCAT('%', #{keyword}, '%') OR A.CRITERIA_MEMO LIKE CONCAT('%', #{keyword}, '%') OR B.SCHOOL_NAME LIKE CONCAT('%', #{keyword}, '%') OR C.DEPARTMENT_NAME LIKE CONCAT('%', #{keyword}, '%') )
AND A.YEAR = #{year}
