mapper.select:
SELECT -- 수시 모집 정보 A.EARLY_ADMISSION_ID, A.ADMISSION_ID, A.CSAT_REFLECTED_SCORE, A.PHYSICAL_REFLECTED_SCORE, A.INTERNAL_REFLECTED_SCORE, A.INTERVIEW_REFLECTED_SCORE, A.TOTAL_REFLECTED_SCORE, A.ACCEPTED_COUNT, A.EARLY_ADMISSION_MEMO, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 학교 정보 B.SCHOOL_NAME, -- 학과 정보 C.DEPARTMENT_NAME, -- 입시 연도 D.YEAR AS ADMISSION_YEAR, -- 등록한 사람, 수정한 사람 E.USER_NAME AS CREATED_USER_NAME, E.USER_PHONE AS CREATED_USER_PHONE, F.USER_NAME AS UPDATED_USER_NAME, F.USER_PHONE AS UPDATED_USER_PHONE FROM EARLY_ADMISSIONS A LEFT JOIN ADMISSIONS D ON D.ADMISSION_ID = A.ADMISSION_ID LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = D.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = D.DEPARTMENT_ID LEFT JOIN USERS E ON E.USER_ID = A.CREATED_BY LEFT JOIN USERS F ON F.USER_ID = A.UPDATED_BY WHERE 1=1
AND ( A.EARLY_ADMISSION_MEMO LIKE CONCAT('%', #{keyword}, '%') OR B.SCHOOL_NAME LIKE CONCAT('%', #{keyword}, '%') OR C.DEPARTMENT_NAME LIKE CONCAT('%', #{keyword}, '%') )
AND D.YEAR = #{admissionYear}
ORDER BY D.YEAR DESC, B.SCHOOL_NAME DESC, C.DEPARTMENT_NAME DESC LIMIT #{offset}, #{listCountPerPage}

SELECT -- 수시 모집 정보 A.EARLY_ADMISSION_ID, A.ADMISSION_ID, A.CSAT_REFLECTED_SCORE, A.PHYSICAL_REFLECTED_SCORE, A.INTERNAL_REFLECTED_SCORE, A.INTERVIEW_REFLECTED_SCORE, A.TOTAL_REFLECTED_SCORE, A.ACCEPTED_COUNT, A.EARLY_ADMISSION_MEMO, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 학교 정보 B.SCHOOL_NAME, -- 학과 정보 C.DEPARTMENT_NAME, -- 입시 연도 D.YEAR AS ADMISSION_YEAR, -- 등록한 사람, 수정한 사람 E.USER_NAME AS CREATED_USER_NAME, E.USER_PHONE AS CREATED_USER_PHONE, F.USER_NAME AS UPDATED_USER_NAME, F.USER_PHONE AS UPDATED_USER_PHONE FROM EARLY_ADMISSIONS A LEFT JOIN ADMISSIONS D ON D.ADMISSION_ID = A.ADMISSION_ID LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = D.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = D.DEPARTMENT_ID LEFT JOIN USERS E ON E.USER_ID = A.CREATED_BY LEFT JOIN USERS F ON F.USER_ID = A.UPDATED_BY WHERE 1=1
AND A.EARLY_ADMISSION_ID = #{earlyAdmissionId}
AND A.ADMISSION_ID = #{admissionId}
LIMIT 1

mapper.update:

UPDATE EARLY_ADMISSIONS SET EARLY_ADMISSION_ID = #{earlyAdmissionId}



    , ADMISSION_ID = #{admissionId}




    , CSAT_REFLECTED_SCORE = #{csatReflectedScore}




    , PHYSICAL_REFLECTED_SCORE = #{physicalReflectedScore}




    , INTERNAL_REFLECTED_SCORE = #{internalReflectedScore}




    , INTERVIEW_REFLECTED_SCORE = #{interviewReflectedScore}




    , TOTAL_REFLECTED_SCORE = #{totalReflectedScore}




    , ACCEPTED_COUNT = #{acceptedCount}




    , EARLY_ADMISSION_MEMO = #{earlyAdmissionMemo}




    , UPDATED_BY = #{updatedBy}



WHERE EARLY_ADMISSION_ID = #{earlyAdmissionId} LIMIT 1

mapper.select:
SELECT COUNT(*) FROM EARLY_ADMISSIONS A LEFT JOIN ADMISSIONS D ON D.ADMISSION_ID = A.ADMISSION_ID LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = D.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = D.DEPARTMENT_ID WHERE 1=1
AND ( A.EARLY_ADMISSION_MEMO LIKE CONCAT('%', #{keyword}, '%') OR B.SCHOOL_NAME LIKE CONCAT('%', #{keyword}, '%') OR C.DEPARTMENT_NAME LIKE CONCAT('%', #{keyword}, '%') )
AND D.YEAR = #{admissionYear}
