mapper.select:
SELECT -- 정시 입시 영어 점수 정보 A.REGULAR_ADMISSION_ENGLISH_ID, A.ADMISSION_ID, A.SUBJECT20_REFLECTED_GRADE1, A.SUBJECT20_REFLECTED_GRADE2, A.SUBJECT20_REFLECTED_GRADE3, A.SUBJECT20_REFLECTED_GRADE4, A.SUBJECT20_REFLECTED_GRADE5, A.SUBJECT20_REFLECTED_GRADE6, A.SUBJECT20_REFLECTED_GRADE7, A.SUBJECT20_REFLECTED_GRADE8, A.SUBJECT20_REFLECTED_GRADE9, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 학교 정보 B.SCHOOL_NAME, -- 학과 정보 C.DEPARTMENT_NAME, -- 입시 연도 D.ADMISSION_YEAR, -- 등록한 사람, 수정한 사람 E.USER_NAME AS CREATED_USER_NAME, E.USER_PHONE AS CREATED_USER_PHONE, F.USER_NAME AS UPDATED_USER_NAME, F.USER_PHONE AS UPDATED_USER_PHONE FROM REGULAR_ADMISSION_ENGLISH A LEFT JOIN ADMISSIONS D ON D.ADMISSION_ID = A.ADMISSION_ID LEFT JOIN SCHOOLS B ON B.SCHOOL_ID = D.SCHOOL_ID LEFT JOIN DEPARTMENTS C ON C.DEPARTMENT_ID = D.DEPARTMENT_ID LEFT JOIN USERS E ON E.USER_ID = A.CREATED_BY LEFT JOIN USERS F ON F.USER_ID = A.UPDATED_BY WHERE 1=1
AND A.REGULAR_ADMISSION_ENGLISH_ID = #{regularAdmissionEnglishId}
AND A.ADMISSION_ID = #{admissionId}
LIMIT 1
