mapper.select:
SELECT -- 사용자 정보 A.USER_ID, A.PASSWORD, A.USER_NAME, A.USER_EMAIL, A.USER_PHONE, A.PROFILE_IMAGE, A.ROLE, A.STATUS, A.DELETE_REQUEST, A.DELETE_REQUESTED_AT, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 등록한 사람, 수정한 사람 B.USER_NAME AS CREATED_USER_NAME, B.USER_PHONE AS CREATED_USER_PHONE, C.USER_NAME AS UPDATED_USER_NAME, C.USER_PHONE AS UPDATED_USER_PHONE FROM USERS A LEFT JOIN USERS B ON B.USER_ID = A.CREATED_BY LEFT JOIN USERS C ON C.USER_ID = A.UPDATED_BY WHERE 1 = 1
AND A.ROLE = #{role}
AND ( A.USER_ID LIKE CONCAT('%', #{keyword}, '%') OR A.USER_NAME LIKE CONCAT('%', #{keyword}, '%') OR A.USER_EMAIL LIKE CONCAT('%', #{keyword}, '%') OR A.USER_PHONE LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY A.USER_NAME ASC LIMIT #{offset}, #{listCountPerPage}

SELECT -- 사용자 정보 A.USER_ID, A.PASSWORD, A.USER_NAME, A.USER_EMAIL, A.USER_PHONE, A.PROFILE_IMAGE, A.ROLE, A.STATUS, A.DELETE_REQUEST, A.DELETE_REQUESTED_AT, A.CREATED_AT, A.UPDATED_AT, A.CREATED_BY, A.UPDATED_BY, -- 등록한 사람, 수정한 사람 B.USER_NAME AS CREATED_USER_NAME, B.USER_PHONE AS CREATED_USER_PHONE, C.USER_NAME AS UPDATED_USER_NAME, C.USER_PHONE AS UPDATED_USER_PHONE FROM USERS A LEFT JOIN USERS B ON B.USER_ID = A.CREATED_BY LEFT JOIN USERS C ON C.USER_ID = A.UPDATED_BY WHERE 1 = 1
AND A.USER_ID = #{userId}
AND A.USER_NAME = #{userName}
AND A.USER_PHONE = #{userPhone}
AND A.USER_EMAIL = #{userEmail}

mapper.update:

UPDATE USERS SET USER_ID = #{userId}



    , PASSWORD = #{password}




    , USER_NAME = #{userName}




    , USER_EMAIL = #{userEmail}




    , USER_PHONE = #{userPhone}




    , PROFILE_IMAGE = #{profileImage}




    , ROLE = #{role}




    , STATUS = #{status}




    , UPDATED_BY = #{updatedBy}






        , DELETE_REQUEST = #{deleteRequest}, DELETE_REQUESTED_AT = CURRENT_TIMESTAMP




        , DELETE_REQUEST = 'N', DELETE_REQUESTED_AT = NULL




WHERE USER_ID = #{userId} LIMIT 1

mapper.select:
SELECT COUNT(*) FROM USERS WHERE 1=1
AND ROLE = #{role}
AND ( USER_ID LIKE CONCAT('%', #{keyword}, '%') OR USER_NAME LIKE CONCAT('%', #{keyword}, '%') OR USER_EMAIL LIKE CONCAT('%', #{keyword}, '%') OR USER_PHONE LIKE CONCAT('%', #{keyword}, '%') )
