mapper.select:
SELECT BRANCH_ID, BRANCH_NAME, BRANCH_ADDRESS, BRANCH_PHONE, CREATED_AT, UPDATED_AT, CREATED_BY, UPDATED_BY FROM BRANCHES WHERE 1=1
AND ( BRANCH_NAME LIKE CONCAT('%', #{keyword}, '%') OR BRANCH_ADDRESS LIKE CONCAT('%', #{keyword}, '%') OR BRANCH_PHONE LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY BRANCH_NAME ASC LIMIT #{offset}, #{listCountPerPage}
